const { Command } = require('discord.js-commando');

module.exports = class ForceQuestionCommand extends Command {
  constructor(client) {
    super(client, {
      name: 'forcequestion',
      group: 'misc',
      memberName: 'forcequestion',
      description: 'Force the Question of the Day to be posted.',
      userPermissions: ['ADMINISTRATOR'], // Adjust the required permissions as needed
    });
  }

  run(message) {
    // Manually post a question and open a thread
    postQuestionOfTheDay();
    console.log('Manual question triggered by user:', message.author.tag);

    return message.reply('Question of the Day forced!');
  }
};
